#include <iostream>
#include <string>
#include <limits>
using namespace std;

class Solution {
public:
    int myAtoi(string s) {
        int i = 0, n = s.size(), sign = 1;
        long result = 0;

        // Step 1: Ignore leading whitespace
        while (i < n && s[i] == ' ') i++;

        // Step 2: Check for sign
        if (i < n && (s[i] == '+' || s[i] == '-')) {
            sign = (s[i] == '-') ? -1 : 1;
            i++;
        }

        // Step 3: Convert digits to integer
        while (i < n && isdigit(s[i])) {
            result = result * 10 + (s[i] - '0');
            if (sign * result < INT_MIN) return INT_MIN;
            if (sign * result > INT_MAX) return INT_MAX;
            i++;
        }

        return sign * result;
    }
};

int main() {
    Solution sol;
    string s = " -42";
    cout << sol.myAtoi(s) << endl; // Expected output: -42
    return 0;
}
