#include <iostream>
#include <limits>
using namespace std;

class Solution {
public:
    bool isPalindrome(int x) {
        if (x < 0 || (x % 10 == 0 && x != 0)) return false;
        int reversed = 0;
        while (x > reversed) {
            int pop = x % 10;
            x /= 10;
            if (reversed > INT_MAX / 10 || (reversed == INT_MAX / 10 && pop > 7)) return false;
            reversed = reversed * 10 + pop;
        }
        return x == reversed || x == reversed / 10;
    }
};

int main() {
    Solution sol;
    int x = 121;
    cout << (sol.isPalindrome(x) ? "true" : "false") << endl; // Expected output: true
    return 0;
}
